* {
  box-sizing: border-box;
  list-style: none;
  text-decoration: none !important;
  margin: 0;
  padding: 0;
  font-family: 'Open Sans', Helvetica, Arial, sans-serif;
  font-size: 10px;
}

html {
  scroll-behavior: smooth;
}

body {
  height: auto;
}

/* Navigation bar start */
.navbar1 {
  position: absolute;
  top: 0;
  position: fixed;
  width: 100%;
  z-index: 99;
}

.navbar1 .nav1 {
  display: flex;
  background-color: rgba(56, 62, 76, 0.5);
  justify-content: center;
  padding-left: 0;
}

.navbar1 .nav1 li {
  margin: 0.5rem 1rem;
  text-align: center;
}

.navbar1 .nav1 li a {
  display: block;
  border: 1px solid rgba(255, 255, 255, 0.3);
  padding: 0.5rem 1.5rem;
  color: aliceblue;
  font-family: 'Open Sans', Helvetica, Arial, sans-serif;
  font-size: 20px;
  text-align: left;
}

.navbar1 .nav1 li a:hover,
.navbar1 .nav1 li a.active {
  background-color: rgba(255, 255, 255, 0.1);
}

/* Sandwhich icon display */

.bi-stack {
  z-index: 1000;
}

.bi-stack {
  color: rgba(255, 255, 255, 1);
  background-color: rgba(255, 255, 255, 0.1);
  font-size: 1.3rem;
  padding: 0.3rem;
  border-radius: 0.6rem;
  position: absolute;
  top: 13px;
  right: 8px;
  position: fixed;
  display: none;
}

/* Sandwhich icon end */

/* Responsive Navbar start */

@media screen and (max-width: 843px) {
  /* .navbar1 {
    display: none;
  } */

  .navbar1 .small-screen {
    display: block;
    max-height: 47px;
    transition: max-height 0.6s ease-out;
    overflow: hidden;
  }

  .navbar1 .small-screen.toggle {
    max-height: 650px;
  }

  .navbar1 .small-screen li {
    margin: 0;
    width: 100%;
    display: block;
    border-bottom: 1px solid rgba(255, 255, 255, 0.3);
    text-align: start;
  }

  .navbar1 .small-screen li a {
    padding: 1rem 0.7rem;
    border: none;
    text-align: start;
  }

  .navbar1 .small-screen li a:not(:first-child) {
    padding: 0.5rem 0.7rem;
  }

  .bi-stack {
    display: block;
  }
}

/* navbar end */

.box1,
.box2,
.box3 {
  height: 400px;
}

/* Header section start */

.box1 {
  color: white;
  background-image: url('./image/img/bg-1a.jpg');
  background-repeat: no-repeat;
  background-position: center;
}

.box1 div {
  text-align: center;
  padding-top: 100px;
}

.box1 div h1 {
  font-size: 70px;
  text-shadow: 2px 2px #000;
}

.box1 div h4 {
  font-size: 25px;
  text-shadow: 1px 1px #333;
}

/* Header section end */

/* Section -02 start */

/* feature-area start */

#feature-area {
  height: auto;
  width: auto;
  padding: 7rem 0;
}

#feature-area div {
  width: 360px;
  margin: auto;
  padding-bottom: 2rem;
  background-color: #f2f2f2;
  text-align: center;
}
#feature-area div img {
  width: 100%;
}

#feature-area div h2 {
  font-size: 2.5rem;
  font-weight: bolder;
  color: #178a27;
  padding: 0.7rem 2rem;
  text-align: center;
  letter-spacing: 0;
}

#feature-area div p {
  text-align: center;
  padding: 0 20px 35px;
  color: #333333;
  margin-top: 10px;
  font-size: 14px;
  line-height: 1.6em;
}

#feature-area div a,
#blog-area .blog-content a {
  color: white;
  background-color: #449d44;
  border-radius: 5px;
  padding: 1rem 3rem;
  font-size: 18px;
}

#feature-area div:not(:first-child) {
  margin-top: 3rem;
}

/* feature-area end */

/* blog content start */

#blog-area .blog-content {
  width: 555px;
  text-align: center;
  margin: auto;
  background-color: #f2f2f2;
  padding-bottom: 2rem;
}

#blog-area .blog-content img {
  width: 100%;
}

#blog-area .blog-content:not(:first-child) {
  margin-top: 3rem;
}

#blog-area .blog-content h2 {
  color: #666666;
  font-size: 2rem;
  font-weight: 700;
  margin-top: 2rem;
  margin-bottom: 1rem;
}

#blog-area .blog-content p {
  padding: 27px;
  color: #333333;
  font-size: 14px;
  line-height: 1.6em;
}

/* section - 02 end */

/* Responsive section - 02 start */

@media screen and (min-width: 843px) {
  #feature-area {
    display: flex;
    justify-content: center;
  }

  #feature-area div:not(:first-child) {
    margin: 0;
    padding: 0;
  }
  #feature-area div:first-child,
  #feature-area div:last-child {
    margin-left: 2rem;
    margin-right: 2rem;
  }

  #blog-area {
    display: flex;
    justify-content: center;
  }

  #blog-area .blog-content {
    margin: 0 2rem;
    flex: 45%;
  }
  #blog-area .blog-content:nth-child(2) {
    margin-top: 0;
  }
}

/* Responsive blog & feature section end */

/* Service section start */

#service-area {
  text-align: center;
  width: 100%;
  height: 316px;
  color: white;
  margin-top: 3rem;
  background-image: url('./image/img/bg-1.jpg');
  background-repeat: no-repeat;
  background-position: center;
}

#service-area h1 {
  text-shadow: 2px 2px #aaa;
  margin-bottom: 20px;
  font-size: 36px;
  font-weight: 500;
  line-height: 1.1;
  padding-top: 80px;
}

#service-area p {
  font-size: 18px;
  line-height: 1.6em;
}

/* service area end here */

/* testimorial-area start */

.testimorial-area {
  margin-top: 60px;
}

.testimorial-area .card {
  background-color: #f2f2f2;
  border: none;
}

.testimorial-area h5 {
  color: #666666;
  font-size: 20px;
  font-weight: 700;
}

.testimorial-area p {
  text-align: left;
  font-size: 16px;
  line-height: 1.6em;
}

.testimorial-area a:hover,
.testimorial-area a:focus {
  background: #e6e6e6 none repeat scroll 0 0;
  color: #666666;
}

.testimorial-area a:hover {
  text-decoration: none;
  outline: 0;
}

.testimorial-area a {
  font-size: 2rem;
  border-radius: 2rem;
}

/* Responsive testimorial-area small-screen */

@media screen and (max-width: 580px) {
  .testimorial-area .card {
    max-width: 300px;
    margin-left: auto;
    margin-right: auto;
  }
}

@media screen and (max-width: 992px) {
  .testimorial-area .card {
    margin-top: 2rem;
  }
}

/* One big column start */

.one-big-column img {
  max-width: 100%;
  height: auto;
}

.one-big-column .card {
  padding: 0;
}

.one-big-column .card .card-title {
  color: #666666;
  font-size: 30px;
  font-weight: 700;
  margin-top: 20px;
  margin-bottom: 10px;
  line-height: 1.1;
}

.one-big-column .card p {
  font-size: 16px;
  line-height: 1.8em;
  margin: 0;
  margin-bottom: 10px;
}

.one-big-column .card a {
  font-size: 25px;
  border: 1px rgba(48, 44, 44, 0.815) solid;
  border-radius: 20px;
  margin: 3rem 1rem;
  margin-left: 2rem;
  padding: 1.5rem 3rem;
  transition: background-color 0.5s;
  text-align: center;
}

.one-big-column .card a:hover {
  color: rgba(235, 224, 224, 0.863);
  background-color: rgb(71, 71, 70);
}

/* form section start */

.form-sect {
  height: auto;
  background-image: url('image/img/bg-3.jpg');
  background-repeat: no-repeat;
  background-position: center;
}

.form-sect .container {
  padding: 5rem 0;
}

.form-sect .container .info {
  background-color: rgba(248, 246, 244, 0.4);
  font-size: 25px;
  text-align: center;
  border-radius: 15px;
  padding: 2rem;
}

.form-sect .container .row input {
  border-radius: 10px;
  background-color: rgba(255, 235, 205, 0.4);
  color: rgba(0, 0, 0, 1);
  display: block;
  width: 100%;
  margin-top: 1rem;
  margin-bottom: 2rem;
  padding: 1rem;
  font-size: 20px;
  outline: none;
  border: none;
  padding-left: 2rem;
}

.form-sect .container .row input:hover,
.form-sect .container .row input:focus,
.form-sect .container .row textarea:hover {
  background-color: rgba(255, 235, 205, 0.6);
  border: 1px solid white;
}

.form-sect .container .row .form-control::placeholder {
  color: rgba(0, 0, 0, 1);
  opacity: 1;
  font-size: 18px;
}

.form-sect .container .row form {
  padding: 0;
}

.form-sect .container textarea {
  border-radius: 10px;
  background-color: rgba(255, 235, 205, 0.4);
  color: rgba(0, 0, 0, 1);
  margin-top: 1rem;
  padding: 1rem;
  font-size: 20px;
  border: none;
  max-height: 400px;
}

@media screen and (max-width: 760px) {
  .form-sect .container textarea {
    max-height: 180px;
  }

  .form-sect .container .info,
  .form-sect .container .row input {
    font-size: 2rem;
  }
}

.form-sect .container .row input[type='submit'] {
  width: 80%;
}
